import React, { useState } from 'react';

const FEATURE_COUNT = 11;

function App() {
  const [features, setFeatures] = useState<number[]>(Array(FEATURE_COUNT).fill(0));
  const [result, setResult] = useState<null | {
    prediction: string;
    probability: string;
    details: string;
  }>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFeatureChange = (idx: number, value: string) => {
    const newFeatures = [...features];
    newFeatures[idx] = Number(value);
    setFeatures(newFeatures);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);
    try {
      const res = await fetch('http://localhost:3000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ features }),
      });
      if (!res.ok) throw new Error('Prediction failed');
      const data = await res.json();
      setResult(data);
    } catch (err) {
      setError('Failed to get prediction');
    } finally {
      setLoading(false);
    }
  };

return (
  <div className="max-w-2xl mx-auto mt-10 font-sans bg-red-400">
    <h1 className="text-3xl font-bold mb-8 text-center text-blue-700">Loan Classifier Demo</h1>
    <form
      onSubmit={handleSubmit}
      className="bg-white shadow-md rounded-lg p-8 space-y-6"
    >
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {features.map((val, idx) => (
          <div key={idx}>
            <label className="block text-gray-700 text-sm font-semibold mb-1">
              Feature {idx + 1}
            </label>
            <input
              type="number"
              value={val}
              onChange={e => handleFeatureChange(idx, e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
              required
            />
          </div>
        ))}
      </div>
      <button
        type="submit"
        disabled={loading}
        className={`w-full py-2 px-4 rounded text-white font-semibold transition ${
          loading
            ? "bg-blue-300 cursor-not-allowed"
            : "bg-blue-600 hover:bg-blue-700"
        }`}
      >
        {loading ? "Predicting..." : "Predict"}
      </button>
      {error && (
        <p className="text-red-600 text-center font-medium">{error}</p>
      )}
    </form>
    {result && (
      <div className="mt-8 bg-green-50 border border-green-200 rounded-lg p-6 shadow">
        <h2 className="text-xl font-bold text-green-700 mb-4">Prediction Result</h2>
        <p className="mb-2">
          <span className="font-semibold">Prediction:</span> {result.prediction}
        </p>
        <p className="mb-2">
          <span className="font-semibold">Probability:</span> {result.probability}
        </p>
        <p>
          <span className="font-semibold">Details:</span> {result.details}
        </p>
      </div>
    )}
  </div>
);
}

export default App;
